            component.set('v.isdisVal', true);
            helper.doInit(component, event, helper);
        }
    },
    "CountryOnChange":function(component, event, helper) {
        var controllerValue = component.find("CountryPicklist").get("v.value");
        var country = event.getSource().get("v.value");
        if(country != '' && country != undefined){
            helper.getStateValues(component, event, helper, country);
        }
        else{
            component.set('v.StateSelected', '--None--');
        }
    },
    "downloadClick":function(component, event, helper) {
        helper.downloadDisclosure(component, event, helper);
    },
    "submitClick":function(component, event, helper) {
        helper.submit(component, event, helper);
    },
    "closeModel":function(component, event, helper) {
        component.set("v.isOpen", false);
