        var fileName = 'No File Selected..';
        if (event.getSource().get("v.files").length > 0) {
            fileName = event.getSource().get("v.files")[0]['name'];
        }
        component.set("v.fileName", fileName);

        if (component.find("fuploader").get("v.files").length > 0) {
            helper.uploadHelper(component, event);
        } else {
            alert('Please Select a Valid File');
        }
    },
    "canadaSignatureB":function(component, event, helper) {
        var caSig = component.find('SignatureCA').get('v.value');
        if(caSig && caSig.length>0 ){
            component.set('v.disableCanadaSubmitButton', false);
        }else{
            component.set('v.disableCanadaSubmitButton', true);
        }
    },
    "downloadDocument":function(component, event, helper) {

        helper.fetchFDDReceiptOnly(component, event, helper);
        var errorTest = component.get('v.receiptError');
        if(errorTest == true){
            component.set('v.displayReceiptError',true);
        }
    },
