        var inputfieldSig = component.find("fieldSig");
        inputfieldSig.isRendered()
        inputfieldSig.showHelpMessageIfInvalid();

        var inputfieldCompName = component.find("fieldCompName");
        inputfieldCompName.isRendered()
        inputfieldCompName.showHelpMessageIfInvalid();

        var inputfieldPrintName = component.find("fieldPrintName");
        inputfieldPrintName.isRendered()
