            var state = result.getState();
            if (component.isValid() && state === "SUCCESS"){
                var response = result.getReturnValue();
                var test = JSON.stringify(response);
                var countrySt= response.CountryState;
                component.set("v.countryStateList",response.CountryState);

                var countrylist =[];
                for(var i=0;i<countrySt.length;i++)
                {
                    countrylist.push(countrySt[i].Country);
                }     
                component.set('v.CountryList',countrylist);



                component.set("v.disRec",response.disc);

                var disc= response.disc;

                if(response.exhibits != null){
                    component.set('v.exhibits',response.exhibits);
                }

                if(response.stateProvinces != null){
                    component.set('v.options', response.stateProvinces);
                }




                if(disc.Date_Received__c != null && disc.Country_Region__c =='Mexico') {
                    component.set('v.disclosureSubmit',true);
                }
                if(disc.Country_Region__c == 'Mexico')
                {

                    component.set('v.isMexico',true);
                    component.set('v.disState',true);
                }
                if(disc.Country_Region__c == 'United States')
                {
                    if(disc.Contact_Country_Region__c != null && disc.Contact_Country_Region__c !=undefined){

                        this.getStateValues(component, event, helper, disc.Contact_Country_Region__c);        
                    }
                    component.set('v.isUnited',true);
                }

                if(disc.Country_Region__c == 'Canada')
                {

                    component.set('v.isCanada',true);
                }

                else
                {                    
                    component.set('v.isUnited',false);
                }


                component.set('v.spinner',false);

                if((disc.Country_Region__c=='United States' || disc.Country_Region__c=='Canada')&& disc.Chain__c != 'Kimpton' && disc.Chain__c != 'Vignette Collection'){
                    component.set('v.isUSorCan',true);
                    console.log('US loop');
                    component.set('v.FranchiseLicense', "franchise");           
                    var date = disc.IHG_Document__r.Issuance_date__c;



                    var months = ["January", "February","March","April","May","June","July","August","September","October","November","December"];
                    component.set('v.issuanceDt',months[parseInt(date.substring(5,7), 10)-1] + ' ' + date.substring(8,10) + ', ' + date.substring(0,4));

                    if(response.heading !=null && response.heading != undefined) {
                        component.set('v.ExhibitHeading',response.heading);
                    }
                }

                else if((disc.Country_Region__c=='United States' || disc.Country_Region__c=='Canada')&&  disc.Chain__c == 'Vignette Collection'){
                    component.set('v.isUSorCan',true);
                    console.log('vignetter loop');
                    component.set('v.FranchiseLicense', "license");           
                    var date = disc.IHG_Document__r.Issuance_date__c;

                    var months = ["January", "February","March","April","May","June","July","August","September","October","November","December"];
                    component.set('v.issuanceDt',months[parseInt(date.substring(5,7), 10)-1] + ' ' + date.substring(8,10) + ', ' + date.substring(0,4));
                    if(response.heading !=null && response.heading != undefined) {
                        component.set('v.ExhibitHeading',response.heading);
                    }
                }

                    else if(disc.Chain__c=='Kimpton' && (disc.Country_Region__c=='United States' || disc.Country_Region__c=='Canada')){

                        console.log('KIMP loop');
                        component.set('v.isKimpton',true);
                        component.set('v.isUSorCan',false);
                        var date = disc.IHG_Document__r.Issuance_date__c;

                        var months = ["January", "February","March","April","May","June","July","August","September","October","November","December"];
                        component.set('v.issuanceDt',months[parseInt(date.substring(5,7), 10)-1] + ' ' + date.substring(8,10) + ', ' + date.substring(0,4));
                        if(response.heading !=null && response.heading != undefined) {
                            component.set('v.ExhibitHeading',response.heading);
                        }
                    }
                        else {
                            component.set('v.isUSorCan',false);
                            component.set('v.isKimpton',false);

                        }                

                if(disc.IHG_Document__c != null){
                    if(!disc.IHG_Document__r.Active__c){
                        component.set('v.disSubmit',true);
                        component.set('v.displayterms',false);
                        component.set('v.disDownload',true);

                        if(disc.Country_Region__c != 'Mexico')
                            component.find('submitbtn').set('v.class','buttonclassInvert');

                    }
                    else if(disc.IHG_Document__r.Active__c && disc.Date_Received__c==null){

                        component.set('v.disSubmit',false);
                        component.set('v.displayterms',true);
                        component.set('v.disDownload',false);

                        if(disc.Country_Region__c != 'Mexico'){

                            component.find('submitbtn').set('v.class','buttonclassbrand');  }                        
                    }
                        else if(disc.IHG_Document__r.Active__c && disc.Date_Received__c!=null){
                            component.set('v.disSubmit',true);
                            component.set('v.displayterms',false);
                            component.set('v.disDownload',false);

                            if(disc.Country_Region__c != 'Mexico')
                                component.find('submitbtn').set('v.class','buttonclassInvert');

                        }
                }

                if(disc.Country_Region__c != 'Mexico' || (disc.Date_Received__c!=null && disc.Country_Region__c == 'Mexico'))

                    this.fetchContentVersion(component,event,helper);

                this.fetchFDDReceiptOnly(component,event,helper);

            }


        });
        $A.enqueueAction(action);
    },
    "submit":function(component, event, helper) {
        var action = component.get("c.submitRecord");
        component.set("v.disSubmit",true);
        component.set("v.disableCanadaSubmitButton",true);

        component.find('submitbtn').set('v.class','buttonclassInvert');
        action.setParams({dis : component.get("v.disRec")
                         });
        action.setCallback(this, function(result){
            var state = result.getState();
            var response = result.getReturnValue();
            if(response){
                if (component.isValid() && state === "SUCCESS"){
                    component.set('v.isSubmitted',true);
                    window.scrollTo(0, 0);
                    setTimeout(function(){component.set('v.isSubmitted',false); }, 5000);

                }
            }else{
                component.set('v.isError',true);
                component.set('v.message','Something went wrong.Please contact System Administrator');
            }


        });
        $A.enqueueAction(action);
        component.set("v.isOpen", false);
    },
    "downloadDisclosure":function(component, event, helper) {
        if (component.isValid()){
            var action = component.get("c.checkReceivedDate");
            action.setParams({disclosureId : component.get("v.disVal")
                             });
            action.setCallback(this, function(result){
                var state = result.getState();
                if (component.isValid() && state === "SUCCESS"){
                    if(result.getReturnValue()) {
                        component.find('submitbtn').set('v.class','buttonclassInvert');
                    }
                    else {
                        component.find('submitbtn').set('v.class','buttonclassbrand'); 
                    }
                }


            });
            $A.enqueueAction(action);
        }        
    },
    "fetchContentVersion":function(component, event, helper) {
        var action = component.get("c.fetchContentVerId");
        action.setParams({disclosureId : component.get("v.disVal"),
                          downloadOnly : component.get("v.isDownloadRecButton")
                         });
        action.setCallback(this, function(result){
            var state = result.getState();

            if (component.isValid() && state === "SUCCESS"){
                component.set("v.conVer",result.getReturnValue()); 

            }

        });
        $A.enqueueAction(action);
    },
    "getStateValues":function(component, event, helper, country) {
        var countrystatelist= component.get('v.countryStateList');
        for(var i=0;i<countrystatelist.length;i++){
            if(countrystatelist[i].Country == country){
                component.set('v.StateList',countrystatelist[i].statevalues);
                if(countrystatelist[i].statevalues == null ||countrystatelist[i].statevalues == undefined ||countrystatelist[i].statevalues.length == 0)
                    component.set('v.StateSelected','--None--');



                break;
            }
        }
    },
    "MAX_FILE_SIZE":4500000,
    "CHUNK_SIZE":750000,
    "uploadHelper":function(component, event) {

        var fileInput = component.find("fuploader").get("v.files");

        var file = fileInput[0];
        var self = this;


        if (file.size > self.MAX_FILE_SIZE) {
            component.set("v.fileName", 'Alert : File size cannot exceed ' + self.MAX_FILE_SIZE + ' bytes.\n' + ' Selected file size: ' + file.size);
            return;
        }


        var objFileReader = new FileReader();

        objFileReader.onload = $A.getCallback(function() {
            var fileContents = objFileReader.result;
            var base64 = 'base64,';
            var dataStart = fileContents.indexOf(base64) + base64.length;

            fileContents = fileContents.substring(dataStart);

            self.uploadProcess(component, file, fileContents);
        });

        objFileReader.readAsDataURL(file);
    },
    "uploadProcess":function(component, file, fileContents) {

        var startPosition = 0;

        var endPosition = Math.min(fileContents.length, startPosition + this.CHUNK_SIZE);


        this.uploadInChunk(component, file, fileContents, startPosition, endPosition, '');
    },
    "uploadInChunk":function(component, file, fileContents, startPosition, endPosition, attachId) {

        var getchunk = fileContents.substring(startPosition, endPosition);
        var action = component.get("c.saveTheChunk");
        action.setParams({
            parentId: component.get("v.disVal"),
            fileName: file.name,
            base64Data: encodeURIComponent(getchunk),
            contentType: file.type,
            fileId: attachId
        });


        action.setCallback(this, function(response) {

            attachId = response.getReturnValue();
            var state = response.getState();
            if (state === "SUCCESS") {

                startPosition = endPosition;
                endPosition = Math.min(fileContents.length, startPosition + this.CHUNK_SIZE);



                if (startPosition < endPosition) {
                    this.uploadInChunk(component, file, fileContents, startPosition, endPosition, attachId);
                } else {
                    component.set('v.disableCanadaSubmitButton', false);
                    alert('File has been uploaded successfully');
                }

            } else if (state === "INCOMPLETE") {
                alert("From server: " + response.getReturnValue());
            } else if (state === "ERROR") {
                var errors = response.getError();
                if (errors) {
                    if (errors[0] && errors[0].message) {
                        console.log("Error message: " + errors[0].message);
                    }
                } else {
                    console.log("Unknown error");
                }
            }
        });

        $A.enqueueAction(action);
    },
    "fetchFDDReceiptOnly":function(component, event, helper) {

        var action = component.get("c.fetchPDFReceipt");

        action.setParams({disclosureId : component.get("v.disVal")
                         });
        action.setCallback(this, function(result){
            var state = result.getState();

            if (component.isValid() && state === "SUCCESS"){
                component.set("v.receiptConVer",result.getReturnValue());
                if(result.getReturnValue() == null){    
                    component.set("v.receiptError",true);
                }else{
                    window.open(result.getReturnValue());
                }
            }


            if(result.getReturnValue()) {
                component.find('submitbtn').set('v.class','buttonclassbrand');
            }

        });
        $A.enqueueAction(action);
    }
  }
