  var def = $A.componentService.getDef("markup://c:DisclosurePrivacyAndTerms");  return $A.lockerService.createForDef(
"return {
  "meta":{
    "name":"c$DisclosurePrivacyAndTerms",
    "extends":"markup://aura:component"
  },
  "controller":{
    "openModel":function(component, event, helper) {

        component.set("v.isOpen", true);
    },
    "closeModel":function(component, event, helper) {

        component.set("v.isOpen", false);
    },
    "Accept":function(component, event, helper) {
        component.set('v.spinner', true);
        component.set('v.pdflist', component.get('v.disclosurepdflist'));
        component.set('v.pdfUrl', component.get('v.discpdfUrl'));
        component.set('v.AcceptPage', false);
        component.set('v.Heading', 'Disclosure');
        component.set('v.docloaded', false);
        component.set('v.spinner', false);
    },
    "doInit":function(component, event, helper) {

        component.set('v.Heading', 'Privacy Policy and Terms & Conditions');
        component.set('v.spinner', true);
        var count =$A.get("{!$Label.c.pagecount}");

        component.set('v.pagecount', count);
        component.set('v.spinner', false);

        helper.getPDFURL(component, event, helper);
    },
    "acknowledge":function(component, event, helper) {
        var acknowledge = component.find('ack').get('v.checked');
        if(acknowledge){
            component.find('acceptbutton').set('v.disabled', false);
            component.find('acceptbutton').set('v.class', 'buttonclassbrand');
            component.set('v.isClicked', true);
        }
        else{
            component.set('v.isClicked', false);
            component.find('acceptbutton').set('v.disabled', true);
            component.find('acceptbutton').set('v.class', 'buttonclassneutral');
        }
    },
    "complete":function(component, event, helper) {
        helper.updatedateAccessed(component, event, helper);
    },
    "spinnertrue":function(component, event, helper) {
        component.set('v.isLoading', true);
    },
    "spinnerfalse":function(component, event, helper) {
        component.set('v.isLoading', false);
    },
