        var action = component.get('c.getUrl');
        action.setParams({disId : component.get('v.disval')});
        action.setCallback(this, function(result){
            var state = result.getState();
            if(state==='SUCCESS'){
                var response = result.getReturnValue();

                if(response != null & response.result){

                    component.set('v.discpdfUrl', response.disclosurepdf);
                    component.set('v.pdfUrl', response.privacypdf);
                    if(response.privacypdf != undefined && response.privacypdf !=null)
                    {
                        component.set('v.isInit', true);
                        component.set('v.docloaded', true);
